                                          <script>
                        $(document).ready(function() {
                           $("#referral_form").attr("action", 'javascript:;');
                           $("#referralotp_form_submit").attr("action", 'javascript:;');
                        
                             // bind parsley to the form
                        $("#referral_form").parsley();
                        });
                        
                        function getEventTarget(e) {
                           e = e || window.event;
                           return e.target || e.srcElement;
                        }
                        var ul = document.getElementById('ref_project');
                        ul.onclick = function(event) {
                           var target = getEventTarget(event);
                           var ref_project = target.innerHTML;
                           document.getElementById('projectss').value = ref_project;
                        };
                        
                        // on form submit
                        $("#referral_form").on('submit', function(event) {
                           // validate form with parsley.
                           $(this).parsley().validate();
                           // if this form is valid
                        
                           if($(this).parsley().isValid()) {
                              $('.parsley-error').hide();
                              var formData = new FormData($("#referral_form")[0]);
                              var base_url = 'index.html';
                              var url = base_url + "pages/referral_form";
                              var count = document.querySelectorAll("input[type=tel]");
                              var count2 = count.length + 1;
                              var mobile = [];
                              var country_code = [];
                              for(var i = 2; i < count2; i++) {
                                 var brochureInput = document.querySelector("input#mobile_number" + i);
                                 var iti = window.intlTelInput(brochureInput, {
                                    utilsScript: "https://www.chandakgroup.com/assets/js/utils.js",
                                    initialCountry: 'in',
                                    placeholderNumberType: '',
                                 });
                                 var code = iti.getSelectedCountryData().dialCode;
                                 var phoneNumber = $("input#mobile_number" + i).val();
                                 mobile.push(phoneNumber);
                                 country_code.push(code);
                              }
                              var ref_country_code = '+' + iti2.getSelectedCountryData().dialCode;
                              // formData.append("isd_code", country_code);
                              formData.append("country_code", country_code);
                              formData.append("ref_country_code", ref_country_code);
                              formData.append("actual_link", 'referral.html');
                              formData.append("meta_title", 'Chandak Referral & Loyalty Program');
                              $(".error").html("");
                              $("#success_message").html("");
                              $("#error_message").html("");
                              $.ajax({
                                 type: "POST",
                                 dataType: "json",
                                 url: url,
                                 data: formData,
                                 //async: false,
                                 global: false,
                                 cache: false,
                                 contentType: false,
                                 processData: false,
                                 beforeSend: function() { 
                                    $("#submit_btn").addClass("disabled");
                                    $("#submit_btn").attr("disabled");
                                  
                                 },
                                 success: function(result) { 
                                    $('input[name=csrf_chandakgroup]').val(result['token']);
                                    $("#final_csrf_token").val(result['token']);
                                    $("#submit_btn").removeClass("disabled");
                                    $("#submit_btn").removeAttr("disabled");
                                    //$("#submit_btn").html("Submit");
                                    if(result["status"] === false) {
                                       if(result["errors"]) {
                                          $.each(result["errors"], function(i, v) {
                                             $("#" + i + "_error").html(v);
                                          });
                                       } else {
                                          $("#error_message").html(result['message']);
                                       }
                                    } else {
                                       $("#referral_form")[0].reset();
                                       $("#referral_form").hide();
                                       $("#success-alert").show();
                                       setTimeout(function() {
                                          window.location.reload();
                                          $("#success-alert").hide();
                                       }, 5000);
                                       
                                    }
                                 },
                                 error: function() {
                                   $("#submit_btn").removeClass("disabled");
                                        $("#submit_btn").removeAttr("disabled");
                                        //$("#submit_btn").html("Submit");
                                        $('input[name=csrf_chandakgroup]').val(result['token']);
                                        $("#final_csrf_token").val(result['token']);
                                        $("#error_message").html("<p style='color:red'>Failed to submit the details</p>", function() {});
                                 }
                              });
                           }
                           // prevent default so the form doesn't submit. We can return true and
                           // the form will be submited or proceed with a ajax request.
                           event.preventDefault();
                        });
                        
                     </script>
