   $(document).on("click", ".select__projects_list .select-dropdown__list-item", function() {
   $(this).parent().removeClass("active");
   });
   $('.select__projects_list .select-dropdown__list-item').click(function() {
   var addText = $(this).text();
   $(this).parent().parent().find('.select-dropdown').empty().append(addText);
   });
   
   function setproject(id) {
   setTimeout(function() {
   var ids = "#project" + id + " li.selected";
   var selText = $(ids).text();
   document.getElementById("projectss_ref" + id).value = selText;
   }, 500);
   }

   $(document).on("click", ".dropdownLabelAnimation", function () {
    // alert('hi')
    $(this).addClass('activeInput');
  });


   function createSelect1() {
     var select = document.getElementsByTagName("select"),
       liElement,
       ulElement,
       optionValue,
       iElement,
       optionText,
       selectDropdown,
       elementParentSpan;

     for (var select_i = 0, len = select.length; select_i < len; select_i++) {
       //console.log('selects init');

       select[select_i].style.display = "none";
       wrapElement1(
         document.getElementById(select[select_i].id),
         document.createElement("div"),
         select_i,
         select[select_i].getAttribute("rel")
       );

       for (var i = 0; i < select[select_i].options.length; i++) {
         liElement = document.createElement("li");
         optionValue = select[select_i].options[i].value;
         optionText = document.createTextNode(select[select_i].options[i].text);
         liElement.className = "select-dropdown__list-item";
         liElement.setAttribute("data-value", optionValue);
         liElement.appendChild(optionText);
         ulElement.appendChild(liElement);

         liElement.addEventListener(
           "click",
           function () {
             displyUl1(this);
           },
           false
         );
       }
     }
     function wrapElement1(el, wrapper, i, placeholder) {
       el.parentNode.insertBefore(wrapper, el);
       wrapper.appendChild(el);

       document.addEventListener("click", function (e) {
         var clickInside = wrapper.contains(e.target);
         if (!clickInside) {
           var menu = wrapper.getElementsByClassName("select-dropdown__list");
           menu[0].classList.remove("active");
         }
       });

       var buttonElement = document.createElement("button"),
         spanElement = document.createElement("span"),
         spanText = document.createTextNode(placeholder);
       iElement = document.createElement("span");
       ulElement = document.createElement("ul");

       wrapper.className = "select-dropdown select-dropdown--" + i;
       buttonElement.className =
         "select-dropdown__button select-dropdown__button--" + i;
       buttonElement.setAttribute("data-value", "");
       buttonElement.setAttribute("type", "button");
       spanElement.className = "select-dropdown select-dropdown--" + i;
       iElement.className = "d__arrow";
       ulElement.className =
         "custom_scroll select-dropdown__list select-dropdown__list--" + i;
       ulElement.id = "select-dropdown__list-" + i;

       wrapper.appendChild(buttonElement);
       spanElement.appendChild(spanText);
       buttonElement.appendChild(spanElement);
       buttonElement.appendChild(iElement);
       wrapper.appendChild(ulElement);
     }

     function displyUl1(element) {
       if (element.tagName == "BUTTON") {
         selectDropdown = element.parentNode.getElementsByTagName("ul");
         //var labelWrapper = document.getElementsByClassName('js-label-wrapper');
         for (var i = 0, len = selectDropdown.length; i < len; i++) {
           selectDropdown[i].classList.toggle("active");
           //var parentNode = $(selectDropdown[i]).closest('.js-label-wrapper');
           //parentNode[0].classList.toggle("active");
         }
       } else if (element.tagName == "LI") {
         var selectId =
           element.parentNode.parentNode.getElementsByTagName("select")[0];
         selectElement1(selectId.id, element.getAttribute("data-value"));
         elementParentSpan =
           element.parentNode.parentNode.getElementsByTagName("span");
         element.parentNode.classList.toggle("active");
         elementParentSpan[0].textContent = element.textContent;
         elementParentSpan[0].parentNode.setAttribute(
           "data-value",
           element.getAttribute("data-value")
         );
       }
     }
     function selectElement1(id, valueToSelect) {
       var element = document.getElementById(id);
       element.value = valueToSelect;
       element.setAttribute("selected", "selected");
     }
     var buttonSelect = document.getElementsByClassName("select-dropdown__button");
     for (var i = 0, len = buttonSelect.length; i < len; i++) {
       buttonSelect[i].addEventListener(
         "click",
         function (e) {
           e.preventDefault();
           displyUl1(this);
         },
         false
       );
     }
   }
</script>
